import AnimatedHeroContent from "./AnimatedHeroContent";
import { useSpring, animated } from "react-spring";




const HeroSection = () => {
    // const props = useSpring({ from: {color: 'green'}, to: { color: 'red'}, delay: 100})
    const animatedStyle = useSpring({
        from: { opacity: 0 },
        opacity: 1
      });
    
    return(
    <div className="hero-wrapper">
        <div className="response-hero">
        <div className="animated-hero-container">
            <AnimatedHeroContent />
            <div className="hero-sub-header-container">
            <div className="sub-header">
                <img src="/shopify-logo.png" alt=""/>
                <h3 className="hero-sub"><span className="shopify-text">Shopify </span> | Partner | Developer | Designer</h3>
            </div>

            <p style={animatedStyle}className="tim-blurb">Hi, I'm Tim - a Bangkok based Shopify devoloper and designer 
          
                       
            </p>
            </div>
        </div>
        <div className="hero-image-container">
        <img  src="/hero-photo.png"/>
        </div>

        </div>
        <div className="hero-social">
            <img src="/github-grey.png" alt=""/>
            <img src="/linkedin-grey.png" alt=""/>
            <img src="/twitter-grey.png" alt=""/>
            <img src="/codepen-grey.png" alt=""/>

        </div>

        
    </div>

)};

export default HeroSection
